import './ChangePassword.css';
import React, { useState } from 'react';
import axios from 'axios';
import { useLocation, useNavigate } from 'react-router-dom';

const ChangePassword = () => {
  const [message, setMessage] = useState(''); // State for displaying messages
  const navigate = useNavigate(); 
  
  const validationSchema = Yup.object({
    createnewpassword: Yup.string()
    .required('New Password is required'),
    confirmpassword: Yup.string()
      .required('Confirm Password is required')
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (newPassword !== confirmPassword) {
      setMessage('Passwords do not match');
      return;
    }
    try {
      await axios.post('http://localhost:5000/api/auth/change-password', { email, newPassword });
      setMessage('Password changed successfully');
      navigate('/login');
    } catch (error) {
      setMessage('Error changing password');
    }
  };

  return (
    <div className="container">
      <div className="form-group col-12">
        <div className="form-container">
          <Formik
            initialValues={{ email: '' }} // Remove password as it's not needed here
            validationSchema={validationSchema}
            onSubmit={handleSubmit}
          >
            {({ isSubmitting }) => (
              <Form className="form-card">
                <h2 className="form-heading">Change Password</h2>
                  {/* Password Field */}
                    <div className="form-group">
                    <label htmlFor="password" className="form-label">Create New Password:</label>
                    <Field
                      type="password"
                      name="password"
                      className="form-control"
                      placeholder="Enter your password"
                    />
                  </div>
                  <ErrorMessage name="password" component="div" className="ErrorMessage1" />

                  {/* Password Field */}
                  <div className="form-group">
                    <label htmlFor="password" className="form-label">Confirm Password:</label>
                    <Field
                      type="password"
                      name="password"
                      className="form-control"
                      placeholder="Enter your password"
                    />
                  </div>
                  <ErrorMessage name="password" component="div" className="ErrorMessage1" />
                {/* Submit Button */}
                <div className="col-12">
                  <button type="submit" className="btn btn-primary submit-btn" disabled={isSubmitting}>
                    {isSubmitting ? 'Submitting...' : 'Change Password'}
                  </button>
                </div>

                {/* Output message */}
                {message && (
                  <div className="row mt-3">
                    <div className="col-12">
                      <div className="output-message">{message}</div>
                    </div>
                  </div>
                )}
              </Form>
            )}
          </Formik>
        </div>
      </div>
    </div>
  );
};

export default ChangePassword;
