import './login.css';
import React from 'react';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as Yup from 'yup';
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faGoogle } from '@fortawesome/free-brands-svg-icons';


const LoginForm = () => {
  // Validation schema using Yup
  const validationSchema = Yup.object({
    email: Yup.string()
      .email('Invalid email format')
      .required('Email is required'),
    password: Yup.string()
      .required('Password is required')
  });

  // Form submission handler
  const handleSubmit = async (values, { setSubmitting, resetForm, setStatus }) => {
    try {
      const response = await axios.post('http://localhost:5000/api/auth/login', values);
      setStatus({ success: 'Login successful!' });
      resetForm(); // Reset form after successful submission
      console.log(response.data);
    } catch (error) {
      setStatus({ error: 'Login failed!' });
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <div>
    <div className="row main_div container mt-5">
      <div className="d-flex align-items-center">
        {/* Left Column for Image */}
        <div className="col-md-7 col-lg-7 col-sm-5 col-12">
          <img
            src="http://knowledgemission.kerala.gov.in/img/official-login.jpg"
            className="img-fluid"
            alt="Login Illustration"
          />
        </div>

        {/* Right Column for Form */}
        <div className="col-md-5 col-lg-5 col-sm-5 col-12">
          <div className="form-container" id='form_cont'>

          <div>
                  <h2 className="form-heading">Welcome to</h2>
                  <h3 className="form-heading1">Login</h3>
                  </div>
                  
            <Formik
              initialValues={{ email: '', password: '' }}
              validationSchema={validationSchema}
              onSubmit={handleSubmit}
              
            >
            
              {({ isSubmitting, status }) => (
                <Form className="form-card">
                  

                  {/* Email Field */}
                
                  <div className="form-group">
                    <label htmlFor="email" className="form-label">Email:</label>
                    <Field
                      type="email"
                      name="email"
                      className="form-control"
                      placeholder="Enter your email"
                    />
                  </div>
                  <ErrorMessage name="email" component="div" className="ErrorMessage" />

                  {/* Password Field */}
                  <div className="form-group">
                    <label htmlFor="password" className="form-label">Password:</label>
                    <Field
                      type="password"
                      name="password"
                      className="form-control"
                      placeholder="Enter your password"
                    />
                  </div>
                  <ErrorMessage name="password" component="div" className="ErrorMessage1" />

                  {/* Forgot Password Link */}
                  <div className="row">
                    <div className="col-12">
                      <a className='forgot' href='#'>Forgot Password?</a>
                    </div>

                    {/* Submit Button */}
                    <div className="col-12">
                      <button type="submit" className="btn btn-primary submit-btn " disabled={isSubmitting}>
                        {isSubmitting ? 'Submitting...' : 'Login'}
                      </button>
                    </div>

                    {/* Output message */}
                    {status && (
                    <div className="row mt-3">
                      <div className="col-12">
                        {status.success && <div className="output-message">{status.success}</div>}
                        {status.error && <div className="output-message">{status.error}</div>}
                      </div>
                    </div>
                  )}
                   
                   <div className="divider-container my-3">
                      <div className="divider-line"></div>
                      <span className="divider-text">or</span>
                      <div className="divider-line"></div>
                    </div>

                    {/* Google Sign-In Button */}
                    <div className="col-12">
                      <button type="button" className="fButoon mt-3">
                      <span className="google-icon me-2">
      <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 48 48" width="20" height="20">
        <path fill="#0D47A1" d="M24 9.5c1.87 0 3.57.64 4.91 1.7l3.65-3.65C29.77 5.67 27.02 4.5 24 4.5c-5.42 0-10 3.12-12.26 7.67l4.82 3.74C17.97 12.11 20.77 9.5 24 9.5z"/>
        <path fill="#0F9D58" d="M24 38.5c3.32 0 6.1-1.1 8.13-3l-4.13-3.2c-1.12.77-2.54 1.23-4 1.23-3.07 0-5.67-2.07-6.6-4.88l-4.84 3.73C15.83 35.65 19.62 38.5 24 38.5z"/>
        <path fill="#F4B400" d="M41.84 24.5c0-1.03-.09-2.03-.26-3h-17.6v6h10.13c-.43 2.19-1.66 4.03-3.4 5.3l4.13 3.2c2.44-2.25 3.99-5.56 3.99-9.5z"/>
        <path fill="#C5221F" d="M17.4 28.65c-.3-.88-.48-1.82-.48-2.65 0-.93.17-1.83.46-2.65l-4.82-3.74c-.91 1.66-1.44 3.55-1.44 5.65 0 2.09.52 4 1.44 5.65l4.84-3.73z"/>
      </svg>
    </span>
            Sign In With Google</button>
                    </div>
                  </div>

                  
                </Form>
              )}
            </Formik>
          </div>
        </div>
      </div>
    </div>
    </div>
  );
};

export default LoginForm;
