import './VerifyOtp.css';
import './VerifyOtp.css';
import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate, useLocation } from 'react-router-dom';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as Yup from 'yup';

const VerifyOtp = () => {
  const [message, setMessage] = useState('');
  const navigate = useNavigate();
  const location = useLocation();
  const { email } = location.state;

  // Define validation schema for OTP
  const validationSchema = Yup.object({
    otp: Yup.string()
      .required('OTP is required')
      .matches(/^\d{6}$/, 'OTP must be a 6-digit number'), // Adjust as per your OTP format
  });

  const handleSubmit = async (values, { setSubmitting }) => {
    try {
      const response = await axios.post('http://localhost:5000/api/verify-otp', { email, otp: values.otp });
      setMessage(response.data.message);
      navigate('/change-password', { state: { email } });
    } catch (error) {
      setMessage('Invalid OTP');
    } finally {
      setSubmitting(false); // Reset submitting state
    }
  };

  return (
    <div className="container">
      <div className="form-group col-12">
        <div className="form-container">
          <Formik
            initialValues={{ otp: '' }} // Set initial values for OTP
            validationSchema={validationSchema}
            onSubmit={handleSubmit}
          >
            {({ isSubmitting }) => (
              <Form className="form-card">
                <h2 className="form-heading">Verify OTP</h2>

                {/* OTP Field */}
                <div className="form-group">
                  <label htmlFor="otp" className="form-label">OTP:</label>
                  <Field
                    type="text"
                    name="otp"
                    className="form-control"
                    placeholder="Enter your OTP"
                  />
                  <ErrorMessage name="otp" component="div" className="ErrorMessage" /> {/* Display validation error */}
                </div>

                {/* Submit Button */}
                <div className="col-12">
                  <button type="submit" className="btn btn-primary submit-btn" disabled={isSubmitting}>
                    {isSubmitting ? 'Submitting...' : 'Verify OTP'}
                  </button>
                </div>

                {/* Output message */}
                {message && (
                  <div className="row mt-3">
                    <div className="col-12">
                      <div className="output-message">{message}</div>
                    </div>
                  </div>
                )}
              </Form>
            )}
          </Formik>
        </div>
      </div>
    </div>
  );
};

export default VerifyOtp;
