import React, { useState } from 'react';
import './upload.css'; 

const FileUpload = () => {
    const [email, setEmail] = useState('');
    const [file, setFile] = useState(null);
    const handleSubmit = async (e) => {
      e.preventDefault();
  
      if (!email || !file) {
          alert("Please fill all fields");
          return;
      }
  
      // Prepare form data for the API call
      const formData = new FormData();
      formData.append('email', email);   // Append email
      formData.append('file', file);     // Append file
  
      try {
          // Call the file upload API
          const response = await fetch('http://localhost:5000/files/upload', {
              method: 'POST',
              body: formData,
          });
          const result = await response.json();
          alert("File uploaded successfully!");
  
          // Provide file download link if necessary
          console.log('Download URL:', result.fileUrl);
      } catch (error) {
          console.error('Error uploading file:', error);
          alert("Failed to upload file.");
      }
  };
  

    return (
        <div className="file-upload-container">
            <form onSubmit={handleSubmit} className="file-upload-form">
                <h2>Upload File</h2>
                <div className="form-group">
                    <label htmlFor="email">Email:</label>
                    <input
                        type="email"
                        id="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        placeholder="Enter your email"
                        required
                    />
                </div>

                <div className="form-group">
                    <label htmlFor="file">Upload File:</label>
                    <input
                        type="file"
                        id="file"
                        onChange={handleFileChange}
                        required
                    />
                </div>

                <button type="submit" className="submit-btn">Submit</button>
            </form>
        </div>
    );
};

export default FileUpload;
