import './ChangePassword.css';
import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as Yup from 'yup';

const ChangePassword = () => {
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  // Validation schema with proper field names
  const validationSchema = Yup.object({
    newPassword: Yup.string()
      .required('New Password is required'),
    confirmPassword: Yup.string()
      .required('Confirm Password is required')
      .oneOf([Yup.ref('newPassword'), null], 'Passwords must match'),
  });

  const handleSubmit = async (values) => {
    const { newPassword, confirmPassword } = values;
    try {
      await axios.post('http://localhost:5000/api/auth/change-password', { newPassword, confirmPassword });
      setMessage('Password changed successfully');
      navigate('/change-password');
    } catch (error) {
      setMessage('Error changing password');
    }
  };

  return (
    <div className="container mt-5">
      <div className="row main_div">
        {/* Left Column for Image */}
        <div className="col-12 col-sm-6 col-md-6 col-lg-6 d-none d-sm-flex justify-content-center align-items-center">
          <img
            src="https://resize.indiatv.in/resize/newbucket/905_509/2023/03/27032023-bing-ai-image-creator-ai-1679915355.jpg"
            className="img-fluid"
            alt="Login Illustration"
          />
        </div>

        {/* Right Column for Form */}
        <div className="col-12 col-sm-6 col-md-6 col-lg-6 col-sm-12">
          <div className="form-container">
            <Formik
              initialValues={{ newPassword: '', confirmPassword: '' }}
              validationSchema={validationSchema}
              onSubmit={handleSubmit}
            >
              {({ isSubmitting }) => (
                <Form className="form-card">
                  <h2 className="form-heading">Change Password</h2>

                  {/* New Password Field */}
                  <div className="form-group">
                    <label htmlFor="newPassword" className="form-label">New Password:</label>
                    <Field
                      type="password"
                      name="newPassword"
                      className="form-control margin-left-3"
                      placeholder="Enter new password"
                    />
                  </div>
                  <ErrorMessage name="newPassword" component="div" className="ErrorMessage" />

                  {/* Confirm Password Field */}
                  <div className="form-group">
                    <label htmlFor="confirmPassword" className="form-label">Confirm Password:</label>
                    <Field
                      type="password"
                      name="confirmPassword"
                      className="form-control"
                      placeholder="Confirm your new password"
                    />
                  </div>
                  <ErrorMessage name="confirmPassword" component="div" className="ErrorMessage1" />

                  {/* Submit Button */}
                  <div className="col-12">
                    <button type="submit" className="btn btn-primary submit-btn" disabled={isSubmitting}>
                      {isSubmitting ? 'Submitting...' : 'Change Password'}
                    </button>
                  </div>

                  {/* Output message */}
                  {message && (
                    <div className="row mt-3">
                      <div className="col-12">
                        <div className="output-message">{message}</div>
                      </div>
                    </div>
                  )}

                </Form>
              )}
            </Formik>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ChangePassword;
