import './user.css';
import React, { useState, useEffect } from 'react';
import axios from 'axios';


const UserForm = () => {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    age: '',
    gender: '',
    profession: '',
  });
  const [errors, setErrors] = useState({});
  const [output, setOutput] = useState('');
  const [dropdownData, setDropdownData] = useState({
    age: [],
    gender: [],
    profession: [],
  });

  useEffect(() => {
    // Fetch dropdown data from backend
    axios
      .get('http://localhost:5000/api/users/dummy-data')
      .then((response) => {
        setDropdownData(response.data);
      })
      .catch((error) => {
        console.error('Error fetching dropdown data:', error);
      });
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;

    // Validate alphabetic characters for name fields
    if ((name === 'firstName' || name === 'lastName') && !/^[A-Za-z]*$/.test(value)) {
      return; // Don't update state if value is invalid
    }

    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const validateForm = () => {
    const newErrors = {};
    const nameRegex = /^[A-Za-z]+$/; // Regex for only letters

    if (!formData.firstName) {
      newErrors.firstName = 'First name is required';
    } else if (!nameRegex.test(formData.firstName)) {
      newErrors.firstName = 'First name can only contain letters';
    }

    if (!formData.lastName) {
      newErrors.lastName = 'Last name is required';
    } else if (!nameRegex.test(formData.lastName)) {
      newErrors.lastName = 'Last name can only contain letters';
    }

    if (!formData.age) {
      newErrors.age = 'Please select your age';
    }

    if (!formData.gender) {
      newErrors.gender = 'Please select your gender';
    }

    if (!formData.profession) {
      newErrors.profession = 'Please select your profession';
    }

    return newErrors;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const validationErrors = validateForm();

    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
    } else {
      setErrors({});
      const userData = { ...formData };

      axios
        .post('http://localhost:5000/api/users', userData)
        .then((response) => {
          setOutput('User created successfully!');
          setFormData({
            firstName: '',
            lastName: '',
            age: '',
            gender: '',
            profession: '',
          });
        })
        .catch(() => {
          setOutput('User creation failed.');
        });
    }
  };

  return (
    <div className="form-container">
  <form onSubmit={handleSubmit} className="form-card">
    <h1 className="form-heading">User Form</h1>

    <div className="row">
      <div className="form-group col-12">
        <label htmlFor="firstName" className="form-label">First Name:</label>
        <input
          type="text"
          className={`form-control ${errors.firstName ? "is-invalid" : ""}`}
          id="firstName"
          name="firstName"
          value={formData.firstName}
          onChange={handleChange}
          placeholder="Enter your first name"
        />
      </div>
      {errors.firstName && <span className="ErrorMessage">{errors.firstName}</span>}

      <div className="form-group col-12">
        <label htmlFor="lastName" className="form-label">Last Name:</label>
        <input
          type="text"
          className={`form-control ${errors.lastName ? "is-invalid" : ""}`}
          id="lastName"
          name="lastName"
          value={formData.lastName}
          onChange={handleChange}
          placeholder="Enter your last name"
        />
      </div>
    </div>
    {errors.lastName && <span className="ErrorMessage">{errors.lastName}</span>}

    <div className="row">
      <div className="form-group col-12">
        <label htmlFor="age" className="form-label">Age:</label>
        <select
          className="form-select"
          id="age"
          name="age"
          value={formData.age}
          onChange={handleChange}
        >
          <option value="">Select Age</option>
          {dropdownData.age.map((ageOption, index) => (
            <option key={index} value={ageOption}>
              {ageOption}
            </option>
          ))}
        </select>
      </div>
      {errors.age && <span className="ErrorMessage">{errors.age}</span>}

      <div className="form-group col-12">
        <label htmlFor="gender" className="form-label">Gender:</label>
        <select
          className="form-select"
          id="gender"
          name="gender"
          value={formData.gender}
          onChange={handleChange}
        >
          <option value="">Select Gender</option>
          {dropdownData.gender.map((genderOption, index) => (
            <option key={index} value={genderOption}>
              {genderOption}
            </option>
          ))}
        </select>
      </div>
      {errors.gender && <span id="error" className="ErrorMessage">{errors.gender}</span>}

      <div className="form-group col-12">
        <label htmlFor="profession" className="form-label">Profession:</label>
        <select
          className="form-select"
          id="profession"
          name="profession"
          value={formData.profession}
          onChange={handleChange}
        >
          <option value="">Select Profession</option>
          {dropdownData.profession.map((professionOption, index) => (
            <option key={index} value={professionOption}>
              {professionOption}
            </option>
          ))}
        </select>
      </div>
    </div>
    {errors.profession && <span id="error1" className="ErrorMessage">{errors.profession}</span>}

    <div className="row">
      <div className="col-12">
        <button type="submit" className="btn btn-primary submit-btn">Submit</button>
      </div>
    </div>

    {output && (
      <div className="row mt-3">
        <div className="col-12">
          <div className="output-message">{output}</div>
        </div>
      </div>
    )}
  </form>
</div>

  );
};

export default UserForm;
