import './ChangePassword.css';
import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as Yup from 'yup';


const ChangePassword = () => {
  const [message, setMessage] = useState(''); // State for displaying messages
  
  const navigate = useNavigate(); 

  const validationSchema = Yup.object({
    newPassword: Yup.string()
      .required('New Password is required'),
    confirmPassword: Yup.string()
      .required('Confirm Password is required')
      .oneOf([Yup.ref('newPassword'), null], 'Passwords must match'), // Ensure passwords match
  });

  const handleSubmit = async (values) => {
    const { newPassword, confirmPassword} = values; // Destructure newPassword from form values
    try {
      await axios.post('http://localhost:5000/api/auth/change-password', { newPassword, confirmPassword});
      setMessage('Password changed successfully');
      navigate('/change-password');
    } catch (error) {
      setMessage('Error changing password');
    }
  };

  return (
    <div className="container">
      <div className="form-group col-12">
        <div className="form-container">
          <Formik
            initialValues={{ newPassword: '', confirmPassword: '' }} // Initial form values
            validationSchema={validationSchema}
            onSubmit={handleSubmit}
          >
            {({ isSubmitting }) => (
              <Form className="form-card">
                <h2 className="form-heading">Change Password</h2>
                
                {/* New Password Field */}
                <div className="form-group col-12 ">
                  <label htmlFor="newPassword" className="form-label">New Password:</label>
                 
                 <div  className="new_passwaord col-12" > 
                  <Field
                  
                    type="password"
                    name="newPassword"
                    className="form-control new_passwaord"
                    placeholder="Enter your new password"
                  />
                  </div>
                  <ErrorMessage name="newPassword" component="div" className="ErrorMessage1" />
                </div>

                {/* Confirm Password Field */}
                <div className="form-group col-12">
                  <label htmlFor="confirmPassword" className="form-label col-12">Confirm Password:</label>
                  <Field
                    type="password"
                    name="confirmPassword"
                    className="form-control"
                    placeholder="Confirm your new password"
                  />
                  <ErrorMessage name="confirmPassword" component="div" className="ErrorMessage1" />
                </div>

                {/* Submit Button */}
                <div className="col-12">
                  <button type="submit" className="btn btn-primary submit-btn" disabled={isSubmitting} onSubmit = {handleSubmit} >
                    {isSubmitting ? 'Submitting...' : 'Change Password'  }
                  </button>
                </div>

                {/* Output message */}
                {message && (
                  <div className="row mt-3">
                    <div className="col-12">
                      <div className="output-message">{message}</div>
                    </div>
                  </div>
                )}
              </Form>
            )}
          </Formik>
        </div>
      </div>
    </div>
  );
};

export default ChangePassword;
