import './user.css';
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const UserForm = () => {
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [age, setAge] = useState('');
    const [gender, setGender] = useState('');
    const [profession, setProfession] = useState('');
    const [dropdownData, setDropdownData] = useState({
        age: [],
        gender: [],
        profession: []
    });

    useEffect(() => {
        // Fetch dropdown data from backend
        axios.get('http://localhost:5000/api/users/dummy-data')
            .then((response) => {
                setDropdownData(response.data);
            })
            .catch((error) => {
                console.error('Error fetching dropdown data:', error);
            });
    }, []);

    const handleSubmit = (e) => {
        e.preventDefault();

        const userData = { firstName, lastName, age, gender, profession };

        axios.post('http://localhost:5000/api/users', userData)
            .then((response) => {
                alert('User created successfully!');
                setFirstName("");
                setLastName("");
                setAge("");
                setGender("");
                setProfession("");
            })
            .catch((error) => {
                console.error('Error creating user:', error);
            });
    };

    return (
        <div className="container my-5">
        <div className="card p-5 shadow-lg" style={{ borderRadius: '12px' }}>
            <h2 className="text-center mb-4 text-primary" style={{ fontWeight: 'bold' }}>User Form</h2>
            <form onSubmit={handleSubmit}>
                <div className="row">
                    <div className="col-12 sm-4 col-md-6 col-lg-8 mb-3">
                        <label htmlFor="firstName" className="form-label">First Name:</label>
                        <input
                            type="text"
                            className="form-control form-control-lg shadow-sm"
                            id="firstName"
                            value={firstName}
                            onChange={(e) => setFirstName(e.target.value)}
                            required
                            placeholder="Enter your first name"
                            style={{ transition: 'all 0.3s ease' }}
                        />
                    </div>
                    <div className="col-12 sm-4 col-md-6 col-lg-8 mb-3">
                        <label htmlFor="lastName" className="form-label">Last Name:</label>
                        <input
                            type="text"
                            className="form-control form-control-lg shadow-sm"
                            id="lastName"
                            value={lastName}
                            onChange={(e) => setLastName(e.target.value)}
                            required
                            placeholder="Enter your last name"
                        />
                    </div>
                </div>
                <div className="row">
                    <div className="col-12 sm-4 col-md-6 col-lg-8 mb-3">
                        <label htmlFor="age" className="form-label">Age:</label>
                        <select
                            className="form-select form-select-lg shadow-sm"
                            id="age"
                            value={age}
                            onChange={(e) => setAge(e.target.value)}
                            style={{ transition: 'all 0.3s ease' }}
                        >
                            <option value="">Select Age</option>
                            {dropdownData.age.map((ageOption, index) => (
                                <option key={index} value={ageOption}>{ageOption}</option>
                            ))}
                        </select>
                    </div>
                    <div className="col-12 sm-4 col-md-6 col-lg-8 mb-3">
                        <label htmlFor="gender" className="form-label">Gender:</label>
                        <select
                            className="form-select form-select-lg shadow-sm"
                            id="gender"
                            value={gender}
                            onChange={(e) => setGender(e.target.value)}
                        >
                            <option value="">Select Gender</option>
                            {dropdownData.gender.map((genderOption, index) => (
                                <option key={index} value={genderOption}>{genderOption}</option>
                            ))}
                        </select>
                    </div>
                    <div className="col-12 sm-4 col-md-6 col-lg-8 mb-3">
                        <label htmlFor="profession" className="form-label">Profession:</label>
                        <select
                            className="form-select form-select-lg shadow-sm"
                            id="profession"
                            value={profession}
                            onChange={(e) => setProfession(e.target.value)}
                        >
                            <option value="">Select Profession</option>
                            {dropdownData.profession.map((professionOption, index) => (
                                <option key={index} value={professionOption}>{professionOption}</option>
                            ))}
                        </select>
                    </div>
                </div>
                <div className="row">
                    <div className="col-12">
                        <button
                            type="submit"
                            className="btn btn-primary btn-lg "
                            style={{ transition: 'background-color 0.3s ease' }}
                            onMouseOver={(e) => e.target.style.backgroundColor = '#0056b3'}
                            onMouseOut={(e) => e.target.style.backgroundColor = '#007bff'}
                        >
                            Submit
                        </button>
                    </div>
                </div>
            </form>
        </div>
    </div>

    );
};

export default UserForm;








