import './user.css';
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const UserForm = () => {
    const [formData, setFormData] = useState({
        firstName: '',
        lastName: '',
        age: '',
        gender: '',
        profession: '',
      });
    const [output, setOutput] = useState('');
    const [errors, setErrors] = useState({}); 
    const [dropdownData, setDropdownData] = useState({
        age: [],
        gender: [],
        profession: []
    });

    useEffect(() => {
        // Fetch dropdown data from backend
        axios.get('http://localhost:5000/api/users/dummy-data')
            .then((response) => {
                setDropdownData(response.data);
            })
            .catch((error) => {
                console.error('Error fetching dropdown data:', error);
            });
    }, []);

    
    const validateForm = () => {
        const newErrors = {};
        const nameRegex = /^[A-Za-z]+$/; // Regex for only letters

        if (!firstName) {
            newErrors.firstName = 'First name is required';
        } else if (!nameRegex.test(firstName)) {
            newErrors.firstName = 'First name can only contain letters';
        }

        if (!lastName) {
            newErrors.lastName = 'Last name is required';
        } else if (!nameRegex.test(lastName)) {
            newErrors.lastName = 'Last name can only contain letters';
        }

        if (!age) {
            newErrors.age = 'Please select your age';
        }

        if (!gender) {
            newErrors.gender = 'Please select your gender';
        }

        if (!profession) {
            newErrors.profession = 'Please select your profession';
        }

        return newErrors;
    };

    const handleSubmit = (e) => {
        e.preventDefault();
       
        const validationErrors = validateForm();
        setErrors(validationErrors);

        // If there are no validation errors, submit the form
        if (Object.keys(validationErrors).length === 0) {

    const userData = { firstName, lastName, age, gender, profession };

    axios.post('http://localhost:5000/api/users', userData)
            .then((response) => {
                setOutput('User created successfully!');
                setFirstName("");
                setLastName("");
                setAge("");
                setGender("");
                setProfession("");
            })
            .catch(() => {
                setOutput("User failed...");
            });
        }
            else {
                setOutput(''); // Clear output if validation fails
            }
    };

    return (
        <div className="form-container">
        <form onSubmit={handleSubmit} className="form-card">
            <div className="row">
                <h1 className="form-heading">User Form</h1>
                <div className="form-group col-12 col-sm-12 col-md-6 col-lg-8">
                    <label htmlFor="firstName" className="form-label">First Name:</label>
                    <input
                        type="text"
                        className={`form-control ${errors.firstName ? 'is-invalid' : ''}`}
                        id="firstName"
                        value={firstName}
                        onChange={(e) => setFirstName(e.target.value)}
                        required
                        placeholder="Enter your first name"/>
                         {errors.firstName && <div className="invalid-feedback">{errors.firstName}</div>}
                </div>
                <div className="form-group col-12 col-sm-12 col-md-6 col-lg-8 ">
                    <label htmlFor="lastName" className="form-label">Last Name:</label>
                    <input
                        type="text"
                        className={`form-control ${errors.lastName ? 'is-invalid' : ''}`}
                        id="lastName"
                        value={lastName}
                        onChange={(e) => setLastName(e.target.value)}
                        required
                        placeholder="Enter your last name"/>
                       {errors.lastName && <div className="invalid-feedback">{errors.lastName}</div>}
                </div>
            </div>
            <div className="row">
                <div className="form-group col-12 col-sm-12 col-md-6 col-lg-8 ">
                    <label htmlFor="age" className="form-label">Age:</label>
                    <select
                        className="form-select"
                        id="age"
                        value={age}
                        onChange={(e) => setAge(e.target.value)}>
                        <option value="">Select Age</option>
                        {dropdownData.age.map((ageOption, index) => (
                            <option key={index} value={ageOption}>{ageOption}</option>
                        ))}
                    </select>
                    {errors.age && <div className="invalid-feedback">{errors.age}</div>}
                </div>

                <div className="form-group col-12 col-sm-12 col-md-6 col-lg-8">
                    <label htmlFor="gender" className="form-label">Gender:</label>
                    <select
                        className={`form-select ${errors.gender ? 'is-invalid' : ''}`}
                        id="gender"
                        value={gender}
                        onChange={(e) => setGender(e.target.value)}>
                        <option value="">Select Gender</option>
                        {dropdownData.gender.map((genderOption, index) => (
                            <option key={index} value={genderOption}>{genderOption}</option>
                        ))}
                    </select>
                   {errors.gender && <div className="invalid-feedback">{errors.gender}</div>}
                </div>

                <div className="form-group col-12 col-sm-12 col-md-6 col-lg-8 ">
                    <label htmlFor="profession" className="form-label">Profession:</label>
                    <select
                        className={`form-select ${errors.profession ? 'is-invalid' : ''}`}
                        id="profession"
                        value={profession}
                        onChange={(e) => setProfession(e.target.value)}
                    >
                        <option value="">Select Profession</option>
                        {dropdownData.profession.map((professionOption, index) => (
                            <option key={index} value={professionOption}>{professionOption}</option>
                        ))}
                    </select>
                    {errors.profession && <div className="invalid-feedback">{errors.profession}</div>}
                </div>
            </div>
            <div className="row">
                <div className="col-12">
                    <button type="submit" className="btn btn-primary submit-btn">Submit</button>
                </div>
            </div>
            {output && (
            <div className="row mt-3">
            <div className="col-12">
             <div className="output-message">
               {output}
              </div>
            </div>
                </div>
                )}
        </form>
        
    </div>

    );
};

export default UserForm;
