import './login.css';
import React, { useState } from 'react';
import axios from 'axios';
import {
  MDBContainer,
  MDBCol,
  MDBRow,
  MDBBtn,
  MDBIcon,
  MDBInput,
  MDBCheckbox
} from 'mdb-react-ui-kit';

const LoginForm = () => {
  const [formData, setFormData] = useState({ email: '', password: '' });
  const [errors, setErrors] = useState({});
  const [output, setOutput] = useState('');

  // Handle input change
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Validate form fields
  const validateForm = () => {
    const newErrors = {};
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/; // Regex for email format

    if (!formData.email) {
      newErrors.email = 'Email is required';
    } else if (!emailRegex.test(formData.email)) {
      newErrors.email = 'Invalid email format';
    }

    if (!formData.password) {
      newErrors.password = 'Password is required';
    }

    return newErrors;
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    const validationErrors = validateForm();
    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
      return;
    }

    try {
      const response = await axios.post('http://localhost:5000/api/auth/login', formData);
      setOutput('Login successful!');
      setFormData({
        email: '',
        password: ''
      });
      console.log(response.data);
    } catch (error) {
      setOutput('Login failed!');
    }
  };

  return (
    <MDBContainer fluid className="p-3 my-5">
    <MDBRow>
      <MDBCol col='10' md='6'>
        <img
          src="https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-login-form/draw2.svg"
          className="img-fluid"
          alt="Phone"
        />
      </MDBCol>

      <MDBCol col='4' md='6'>
        <form onSubmit={handleSubmit} className="form-container-inline">
          <h2 className="form-heading">Login</h2>

          {/* Email Field */}
          <div className="form-group-inline">
            <label htmlFor="email" className="form-label-inline">Email:</label>
            <MDBInput
              wrapperClass='mb-4'
              id='formControlLg'
              type='email'
              size="lg"
              name="email"
              value={formData.email}
              onChange={handleChange}
              className={errors.email ? 'is-invalid' : ''}
            />
          </div>
          {errors.email && <span className="ErrorMessage">{errors.email}</span>}

          {/* Password Field */}
          <div className="form-group-inline">
            <label htmlFor="password" className="form-label-inline">Password:</label>
            <MDBInput
              wrapperClass='mb-4'
              id='formControlLg'
              type='password'
              size="lg"
              name="password"
              value={formData.password}
              onChange={handleChange}
              className={errors.password ? 'is-invalid' : ''}
            />
          </div>
          {errors.password && <span className="ErrorMessage">{errors.password}</span>}

          <div className="d-flex justify-content-between mx-4 mb-4">
            <MDBCheckbox name='flexCheck' value='' id='flexCheckDefault' label='Remember me' />
            <a href="#!">Forgot password?</a>
          </div>

          <MDBBtn type="submit" className="mb-4 w-100" size="lg">Sign in</MDBBtn>

          <div className="divider d-flex align-items-center my-4">
            <p className="text-center fw-bold mx-3 mb-0">OR</p>
          </div>

          {/* Social Buttons */}
          <MDBBtn className="mb-4 w-100" size="lg" style={{ backgroundColor: '#3b5998' }}>
            <MDBIcon fab icon="facebook-f" className="mx-2" />
            Continue with Facebook
          </MDBBtn>

          <MDBBtn className="mb-4 w-100" size="lg" style={{ backgroundColor: '#55acee' }}>
            <MDBIcon fab icon="twitter" className="mx-2" />
            Continue with Twitter
          </MDBBtn>

          {/* Output Message */}
          {output && (
            <div className="output-message">{output}</div>
          )}
        </form>
      </MDBCol>
    </MDBRow>
  </MDBContainer>
);
};

export default LoginForm;
