import './ForgotPassword.css';
import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as Yup from 'yup';

const ForgotPassword = () => {
  const validationSchema = Yup.object({
    email: Yup.string()
      .email('Invalid email format')
      .required('Email is required'),
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/api/forgot-password', { email });
      setMessage(response.data.message);
      navigate('/verify-otp', { state: { email } });
    } catch (error) {
      setMessage('Error sending OTP');
    }
  };

  return (
    <div className="container">

        <div className="form-group col-12">
          <div className="form-container ">
            <Formik
              initialValues={{ email: '', password: '' }}
              validationSchema={validationSchema}
              onSubmit={handleSubmit}
            >
              {({ isSubmitting, status }) => (
                <Form className="form-card">
                  <h2 className="form-heading">Welcome to</h2>
                  <h3 className="form-heading1">Login</h3>

                  {/* Email Field */}
                  <div className="form-group">
                    <label htmlFor="email" className="form-label">Email:</label>
                    <Field
                      type="email"
                      name="email"
                      className="form-control"
                      placeholder="Enter your email"
                    />
                  </div>
                  <ErrorMessage name="email" component="div" className="ErrorMessage" />

                  {/* Submit Button */}
                  <div className="col-12">
                    <button type="submit" className="btn btn-primary submit-btn" disabled={isSubmitting}>
                      {isSubmitting ? 'Submitting...' : 'Send Otp'}
                    </button>
                  </div>

                  {/* Output message */}
                  {status && (
                    <div className="row mt-3">
                      <div className="col-12">
                        {status.success && <div className="output-message">{status.success}</div>}
                        {status.error && <div className="output-message">{status.error}</div>}
                      </div>
                    </div>
                  )}
                  
                </Form>
              )}
            </Formik>
          </div>
        </div>
    </div>

  );
};

export default ForgotPassword;
