import './login.css';
import React, { useState } from 'react';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import axios from 'axios';

const LoginForm = () => {
  const [loginError, setLoginError] = useState('');

  const formik = useFormik({
    initialValues: {
      email: '',
      password: '',
    },
    validationSchema: Yup.object({
      email: Yup.string()
        .email('Invalid email address')
        .required('Email is required'),
      password: Yup.string()
        .min(6, 'Password must be at least 6 characters long')
        .required('Password is required'),
    }),
    onSubmit: async (values) => {
      try {
        const response = await axios.post('http://localhost:5000/api/login', values);
        alert('Login successful');
        // Handle success (store token, redirect, etc.)
      } catch (error) {
        setLoginError('Invalid email or password');
      }
    },
  });

  return (
    <div className="form-container">
      <form onSubmit={formik.handleSubmit} className="form-card">
        <h1 className="form-heading">Login</h1>

        <div className="form-group">
          <label htmlFor="email" className="form-label">Email</label>
          <input
            type="email"
            id="email"
            name="email"
            className={`form-control ${formik.errors.email && formik.touched.email ? 'is-invalid' : ''}`}
            onChange={formik.handleChange}
            onBlur={formik.handleBlur}
            value={formik.values.email}
            placeholder="Enter your email"
          />
        </div>
        {formik.errors.email && formik.touched.email ? (
            <span className="ErrorMessage">{formik.errors.email}</span>
          ) : null}

        <div className="form-group">
          <label htmlFor="password" className="form-label">Password</label>
          <input
            type="password"
            id="password"
            name="password"
            className={`form-control ${formik.errors.password && formik.touched.password ? 'is-invalid' : ''}`}
            onChange={formik.handleChange}
            onBlur={formik.handleBlur}
            value={formik.values.password}
            placeholder="Enter your password"
          />
        </div>
        {formik.errors.password && formik.touched.password ? (
            <span id="error" className="ErrorMessage">{formik.errors.password}</span>
          ) : null}


     <div className="row">
      <div className="col-12">
        <button type="submit" className="btn btn-primary submit-btn">Submit</button>
      </div>
    </div>

    {loginError && (
      <div className="row mt-3">
        <div className="col-12">
          <div className="output-message">{loginError}</div>
        </div>
      </div>
    )}
    
      </form>
    </div>
  );
};

export default LoginForm;
