import './ForgotPassword.css';
import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as Yup from 'yup';

const ForgotPassword = () => {
  const [message, setMessage] = useState(''); // State for displaying messages
  const navigate = useNavigate(); // Hook to programmatically navigate

  const validationSchema = Yup.object({
    email: Yup.string()
      .email('Invalid email format')
      .required('Email is required'),
  });

  const handleSubmit = async (values, { setSubmitting }) => {
    try {
      const response = await axios.post('http://localhost:5000/api/auth/forgot-password', { email: values.email }); // Ensure the API endpoint is correct
      setMessage(response.data.message);
      navigate('/verify-otp', { state: { email: values.email } }); // Pass the email to the next page
    } catch (error) {
      setMessage('Error sending OTP'); // Handle error message
    } finally {
      setSubmitting(false); // Reset submitting state
    }
  };

  return (
    <div className="container mt-5">
      <div className="row main_div">
        {/* Left Column for Image */}
        <div className="col-12 col-sm-6  col-md-6 col-lg-6 d-none d-sm-flex  justify-content-center align-items-center">
          <img
            src="http://knowledgemission.kerala.gov.in/img/official-login.jpg"
            className="img-fluid"
            alt="Login Illustration"
          />
        </div>
        
        {/* Right Column for Form */}
        <div className="col-12 col-sm-6 col-md-6 col-lg-6 col-sm-12">
          <div className="form-container ">
            <Formik
              initialValues={{ email: ''}}
              validationSchema={validationSchema}
              onSubmit={handleSubmit}
            >
              {({ isSubmitting, status }) => (
                <Form className="form-card">
                  <h2 className="form-heading">Forgot Password</h2>

                  {/* Email Field */}
                  <div className="form-group">
                    <label htmlFor="email" className="form-label">Email:</label>
                    <Field
                      type="email"
                      name="email"
                      className="form-control"
                      placeholder="Enter your email"
                    />
                  </div>
                  <ErrorMessage name="email" component="div" className="ErrorMessage" />

                  {/* Submit Button */}
                  <div className="col-12">
                    <button type="submit" className="btn btn-primary submit-btn" disabled={isSubmitting}>
                      {isSubmitting ? 'Submitting...' : 'Login'}
                    </button>
                  </div>

                  {/* Output message */}
                  {message && (
                    <div className="row mt-3">
                      <div className="col-12">
                        <div className="output-message">{message}</div>
                      </div>
                    </div>
                  )}

                </Form>
              )}
            </Formik>
          </div>
        </div>


      </div>
    </div>
  );
};

export default ForgotPassword;
