import './login.css';
import React, { useState } from 'react';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as Yup from 'yup';
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEye, faEyeSlash } from '@fortawesome/free-solid-svg-icons';

const LoginForm = () => {
  // State to handle password visibility
  const [showPassword, setShowPassword] = useState(false);

  // Toggle password visibility
  const togglePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };

  // Validation schema using Yup
  const validationSchema = Yup.object({
    email: Yup.string()
      .email('Invalid email format')
      .required('Email is required'),
    password: Yup.string()
      .required('Password is required')
  });

  // Form submission handler
  const handleSubmit = async (values, { setSubmitting, resetForm, setStatus }) => {
    try {
      const response = await axios.post('http://localhost:5000/api/auth/login', values);
      setStatus({ success: 'Login successful!' });
      resetForm(); // Reset form after successful submission
      console.log(response.data);
    } catch (error) {
      setStatus({ error: 'Login failed!' });
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <div className="row main_div container mt-5">
      <div className="d-flex align-items-center">
        {/* Left Column for Image */}
        <div className="col-md-6 col-sm-5 col-12">
          <img
            src="http://knowledgemission.kerala.gov.in/img/official-login.jpg"
            className="img-fluid"
            alt="Login Illustration"
          />
        </div>

        {/* Right Column for Form */}
        <div className="col-md-6 col-sm-5 col-12">
          <div className="form-container" id="form_cont">
            <Formik
              initialValues={{ email: '', password: '' }}
              validationSchema={validationSchema}
              onSubmit={handleSubmit}
            >
              {({ isSubmitting, status }) => (
                <Form className="form-card">
                  <h2 className="form-heading">Welcome to</h2>
                  <h3 className="form-heading1">Login</h3>

                  {/* Email Field */}
                  <div className="form-group">
                    <label htmlFor="email" className="form-label">Email:</label>
                    <Field
                      type="email"
                      name="email"
                      className="form-control"
                      placeholder="Enter your email"
                    />
                  </div>
                  <ErrorMessage name="email" component="div" className="ErrorMessage" />

                  {/* Password Field */}
                  <div className="form-group">
                    <label htmlFor="password" className="form-label">Password:</label>
                    <div style={{ position: 'relative' }}>
                      <Field
                        type={showPassword ? 'text' : 'password'}
                        name="password"
                        className="form-control"
                        placeholder="Enter your password"
                      />
                      {/* Eye icon to toggle password visibility */}
                      <span className="eye-icon" onClick={togglePasswordVisibility}>
                        <FontAwesomeIcon icon={showPassword ? faEyeSlash : faEye} />
                      </span>
                    </div>
                  </div>
                  <ErrorMessage name="password" component="div" className="ErrorMessage1" />

                  {/* Forgot Password Link */}
                  <div className="row">
                    <div className="col-12">
                      <a href="#">Forgot Password?</a>
                    </div>

                    {/* Submit Button */}
                    <div className="col-12">
                      <button type="submit" className="btn btn-primary submit-btn mt-3" disabled={isSubmitting}>
                        {isSubmitting ? 'Submitting...' : 'Submit'}
                      </button>
                    </div>

                    {/* Output message */}
                    {status && (
                      <div className="row mt-3">
                        <div className="col-12">
                          {status.success && <div className="output-message">{status.success}</div>}
                          {status.error && <div className="output-message">{status.error}</div>}
                        </div>
                      </div>
                    )}
                    <hr />

                    {/* Google Sign-In Button */}
                    <div className="col-12">
                      <button type="button" className="btn btn-primary submit-btn mt-3">Sign In With Google</button>
                    </div>
                  </div>
                </Form>
              )}
            </Formik>
          </div>
        </div>
      </div>
    </div>
  );
};

export default LoginForm;
