import React, { useState } from 'react';
import axios from 'axios';  // Import Axios
import './upload.css';  // Assuming you have a CSS file for styling

const FileUpload = () => {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [file, setFile] = useState(null);
    const [message, setMessage] = useState('');
    const [errors, setErrors] = useState({});  // State to store validation errors

    // Handle file change
    const handleFileChange = (e) => {
        setFile(e.target.files[0]);  // Update the file state with the selected file
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        // Reset errors
        setErrors({});

        // Validate form fields
        const newErrors = {};
        if (!name) newErrors.name = "Name is required.";
        if (!email) newErrors.email = "Email is required.";
        if (!file) newErrors.file = "File is required.";

        // Check if there are any validation errors
        if (Object.keys(newErrors).length > 0) {
            setErrors(newErrors);
            return;  // Stop the form submission
        }

        // Prepare form data for the API call
        const formData = new FormData();
        formData.append('name', name);
        formData.append('email', email);   // Append email
        formData.append('file', file);      // Append file

        try {
            // Call the file upload API using Axios
            const response = await axios.post('http://localhost:5000/files/upload', formData);
            
            // Handle success response
            setMessage("File uploaded successfully!");

            // Provide file download link if necessary
            console.log('Download URL:', response.data.fileUrl);

            // Clear the form after submission
            setName('');
            setEmail('');
            setFile(null);
        } catch (error) {
            // Handle error response
            console.error('Error uploading file:', error);
            setMessage("Failed to upload file.");
        }
    };

    return (
        <div className="file-upload-container">
            <form onSubmit={handleSubmit} className="file-upload-form">
                <h2>Upload File</h2>
                
                <div className="form-group" id='group'>
                    <label htmlFor="name">Full Name:</label>
                    <input
                        type="text"
                        id="name"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        placeholder="Enter your full name"
                        required
                    />
                    {errors.name && <div className="error-message">{errors.name}</div>} {/* Display name error */}
                </div>

                <div className="form-group">
                    <label htmlFor="email">Email:</label>
                    <input
                        type="email"
                        id="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        placeholder="Enter your email"
                        required
                    />
                    {errors.email && <div className="error-message">{errors.email}</div>} {/* Display email error */}
                </div>

                <div className="form-group">
                    <label htmlFor="file">Upload File:</label>
                    <input
                        type="file"
                        id="file"
                        onChange={handleFileChange}
                        required
                    />
                    {errors.file && <div className="error-message">{errors.file}</div>} {/* Display file error */}
                </div>

                <button type="submit" className="submit-btn">Submit</button>
                {message && <div className="success-message">{message}</div>}
            </form>
        </div>
    );
};

export default FileUpload;
