import './login.css';
import React, { useState } from 'react';
import axios from 'axios';

const LoginForm = () => {
  const [formData, setFormData] = useState({ email: '', password: '' });
  const [errors, setErrors] = useState({});
  const [output, setOutput] = useState('');

  // Handle input change
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Validate form fields
  const validateForm = () => {
    const newErrors = {};
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

    if (!formData.email) {
      newErrors.email = 'Email is required';
    } else if (!emailRegex.test(formData.email)) {
      newErrors.email = 'Invalid email format';
    }

    if (!formData.password) {
      newErrors.password = 'Password is required';
    }

    return newErrors;
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    const validationErrors = validateForm();
    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
      return;
    }

    try {
      const response = await axios.post('http://localhost:5000/api/auth/login', formData);
      setOutput('Login successful!');
      setFormData({
        email: '',
        password: ''
      });
      console.log(response.data);
    } catch (error) {
      setOutput('Login failed!');
    }
  };

  return (
    <div className="main_div container-fluid mt-5">
    <div className="row d-flex justify-content-center align-items-center">
      {/* Left Column for Image */}
      <div className="col-lg-6 col-md-6 col-sm-12 d-none d-md-flex justify-content-center align-items-center bg-image">
        <img
          src="https://images.pexels.com/photos/3184287/pexels-photo-3184287.jpeg?auto=compress&cs=tinysrgb&w=600"
          className="img-fluid login-img"
          alt="Login Illustration"
        />
      </div>

      {/* Right Column for Form */}
      <div className="col-lg-5 col-md-6 col-sm-12 login-form">
        <div className="form-container shadow p-5">
          <form onSubmit={handleSubmit} className="form-card">
            <h2 className="form-heading text-center mb-4">Login</h2>

            <div className="form-group mb-4">
              <label htmlFor="email" className="form-label">Email:</label>
              <input
                type="email"
                className={`form-control ${errors.email ? 'is-invalid' : ''}`}
                id="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                placeholder="Enter your email"
              />
              {errors.email && <span className="ErrorMessage">{errors.email}</span>}
            </div>

            <div className="form-group mb-4">
              <label htmlFor="password" className="form-label">Password:</label>
              <input
                type="password"
                className={`form-control ${errors.password ? 'is-invalid' : ''}`}
                id="password"
                name="password"
                value={formData.password}
                onChange={handleChange}
                placeholder="Enter your password"
              />
              {errors.password && <span className="ErrorMessage">{errors.password}</span>}
            </div>

            <div className="form-group d-flex justify-content-between mb-4">
              <a href="#" className="forgot-password">Forgot Password?</a>
            </div>

            <div className="text-center">
              <button type="submit" className="btn btn-primary w-100 mb-3">Login</button>
            </div>

            <div className="text-center">
              <button type="button" className="btn btn-outline-primary w-100">
                Sign In With Google
              </button>
            </div>

            {output && (
              <div className="mt-3 text-center output-message">{output}</div>
            )}
          </form>
        </div>
      </div>
    </div>
  </div>
);
};

export default LoginForm;





